% VG network:

s(vg,_/'RB',vg). 

s(vg,_/T,2) :- member(T,['TO','MD','VBD','VBZ','VBP']).


s(vg,W/T,8) :- member(T,['VBD','VBP','VBZ','VBG','VBN']),
	       adj_taking_verb(W).
s(vg,_/T,3) :- member(T,['VBD','VBP','VBZ','VBG','VBN','VB']).


%s(vg,jump,2).


s(2,_/'RB',2).
s(2,_/'VB',3).
