final(6).
final(7).
final(8).
final(9).

% conditions:
%adj_taking_verb(be).
%negative_adverb(negadv).

adj_taking_verb(W) :-
	member(W,[is,'isn\'t',was,'wasn\'t',am,are,'aren\'t',were,'weren\'t',be,been,
		  being,be,seem,seems,seemed,seeming,appear,appears,appeared,
		  appearing,look,looks,looking,looked,
		  'Is','Isn\'t','Was','Wasn\'t','Am','Are','Aren\'t','Were','Weren\'t',
		  'Been','Being','Be','Seem','Seems','Seemed','Seeming','Appear','Appears','Appeared',
		  'Appearing','Look','Looks','Looking','Looked']).

negative_adverb(W):-
	member(W,[not,'Not',never,'Never']).


np_final_det(Word):-
	\+member(Word,['The',the,a,'A',an,'An',no,'No',every,'Every']).




np_final_adj(A) :-
	member(A,[
former,first,second,third,fourth,fifth,sixth,seventh,eighth,ninth,tenth,
final,
				%such,like,
		  next,last,opposite,original,many,few,right,left,good,bad,
similar,unusual,favourite,past,future,latter,former,east,west,south,north,
own,specific,new,old,other,same,different]).


